mapper.insert:
INSERT INTO BOARD
(
    TITLE
,   CONTENT
,   UID
,   DATE
)
VALUE
(
    #{title}
,   #{content}
,   #{uid}
,   DATE_ADD(now(), INTERVAL 9 HOUR)
)

mapper.update:
UPDATE  BOARD
SET     TITLE       = #{title}
    ,   CONTENT     = #{content}
    ,   MODIFYDATE  = DATE_ADD(now(), INTERVAL 9 HOUR)
WHERE   UID         = #{uid}
AND     DATE        = #{date}

mapper.delete:
DELETE  FROM    BOARD
WHERE   TITLE   = #{title}
AND     UID     = #{uid}
AND     DATE    = #{date}
