mapper.select:
SELECT  CASE
        WHEN
        EXISTS  (
                SELECT  1
                FROM    USER
                WHERE   UID = #{userId}
                )
        THEN    0
        ELSE    1
        END AS  result;

mapper.insert:
INSERT  INTO    USER
(
   UID
,  PWD
,  NAME
,  BIRTH
,  PHONE
,  ZIPCODE
,  ADDRESS
,  DETAILADDRESS
)
VALUES
(
    #{userId}
,   #{userPwd}
,   #{userName}
,   #{userBirth}
,   #{userPhone}
,   #{userZipcode}
,   #{userAddress}
,   #{userDetailAddress}
)

mapper.update:
UPDATE  USER
SET     PWD             =   #{userPwd}
    ,   NAME            =   #{userName}
    ,   BIRTH           =   #{userBirth}
    ,   PHONE           =   #{userPhone}
    ,   ZIPCODE         =   #{userZipcode}
    ,   ADDRESS         =   #{userAddress}
    ,   DETAILADDRESS   =   #{userDetailAddress}
WHERE   UID             =   #{userId}

mapper.select:
SELECT  PWD
FROM    USER
WHERE   UID =   #{email};
