mapper.insert:
INSERT  INTO    USER
(
   UID
,  PWD
,  NAME
,  BIRTH
,  PHONE
,  ZIPCODE
,  ADDRESS
)
VALUES
(
    #{userId}
,   #{userPwd}
,   #{userName}
,   #{userBirth}
,   #{userPhone}
,   #{userZipcode}
,   #{userAddress}
)

mapper.select:
SELECT    CASE    WHEN
                      EXISTS (
                              SELECT	1
                              FROM	    USER
                              WHERE	    UID	=	#{email}
                              AND	    PWD	=	#{pwd}
                             )
                  THEN	1
                  ELSE	0
                  END	AS	result;
